CREATE TABLE `db_er`.`empresa` ( `idEmpresa` INT NOT NULL , `SiglaEmpresa` VARCHAR(15) NOT NULL , `NomeEmpresa` VARCHAR(30) NOT NULL ,PRIMARY KEY(`idEmpresa`));
CREATE TABLE `db_er`.`produto` ( `idProduto` INT NOT NULL , `AbrProduto` VARCHAR(10) NOT NULL , `Descricao` VARCHAR(30) NOT NULL ,PRIMARY KEY(`idProduto`));
CREATE TABLE `db_er`.`atividade` ( `idAtividade` INT NOT NULL , `AbrAtividade` VARCHAR(10) NOT NULL , `Descricao` VARCHAR(30) NOT NULL ,PRIMARY KEY(`idAtividade`));
CREATE TABLE `db_er`.`custocliente` ( `idCustoCliente` INT NOT NULL , `ValorHH` FLOAT(8,2) , `idEmpresa` INT NOT NULL , `idProduto` INT , `DtLimite` DATE ,PRIMARY KEY(`idCustoCliente`),FOREIGN KEY (`idEmpresa`) REFERENCES empresa(`idEmpresa`),FOREIGN KEY (`idProduto`) REFERENCES produto(`idProduto`));
CREATE TABLE `db_er`.`pessoa` ( `idPessoa` INT NOT NULL , `idEmpresa` INT NOT NULL , `Apelido` VARCHAR(10) NOT NULL , `Nome` VARCHAR(30) NOT NULL , `email` VARCHAR(30) , `Telefone` VARCHAR(30),PRIMARY KEY(`idPessoa`),FOREIGN KEY (`idEmpresa`) REFERENCES empresa(`idEmpresa`));
CREATE TABLE `db_er`.`trabalhosolicitado` ( `idTrabalhoSolicitado` INT NOT NULL , `idPessoa` INT , `CodTrabalho` VARCHAR(10) NOT NULL , `Titulo` VARCHAR(120) NOT NULL , `Descricao` VARCHAR(500) NOT NULL , `DtInicioAnalise` DATE , `DtEntregaET` DATE , `DtAceiteET` DATE , `DtAceiteProposta` DATE , `DtInicioProgr` DATE , `DtEntregaProgt` DATE  , `DtAceitacaoTrabalho` DATE , `Custo` FLOAT(8,2) , `ValorAcordado` FLOAT(8,2) , `DtPgto` DATE  ,PRIMARY KEY(`idTrabalhoSolicitado`),FOREIGN KEY (`idPessoa`) REFERENCES pessoa(`idPessoa`));
CREATE TABLE `db_er`.`atividadesprevistas` ( `idAtividadesPrevistas` INT NOT NULL , `idTrabalhoSolicitado` INT NOT NULL , `idProduto` INT NOT NULL , `idAtividade` INT NOT NULL , `HorasPrevistas` FLOAT(8,2) NOT NULL , `DetalheAtividade` VARCHAR(120) , `Obs` VARCHAR(500) ,PRIMARY KEY(`idAtividadesPrevistas`),FOREIGN KEY (`idTrabalhoSolicitado`) REFERENCES trabalhosolicitado(`idTrabalhoSolicitado`),FOREIGN KEY (`idProduto`) REFERENCES produto(`idProduto`),FOREIGN KEY (`idAtividade`) REFERENCES atividade(`idAtividade`));
CREATE TABLE `db_er`.`eventos` ( `idEvento` VARCHAR(10) NOT NULL , `idTrabalhoSolicitado` INT NOT NULL , `DataEvento` VARCHAR(10) NOT NULL , `Anotacoes` VARCHAR(10) NOT NULL ,PRIMARY KEY(`idEvento`),FOREIGN KEY (`idTrabalhoSolicitado`) REFERENCES trabalhosolicitado(`idTrabalhoSolicitado`));
CREATE TABLE `db_er`.`trabalhorealizado` ( `idTrabalhoRealizado` VARCHAR(10) NOT NULL , `idAtividadesPrevistas` INT NOT NULL , `idPessoa` INT NOT NULL , `DtTrabRealizado` DATE NOT NULL , `Detalhe` VARCHAR(200) NOT NULL , `HorasAplicadas` FLOAT(8,2) NOT NULL ,PRIMARY KEY(`idTrabalhoRealizado`),FOREIGN KEY (`idAtividadesPrevistas`) REFERENCES atividadesprevistas(`idAtividadesPrevistas`),FOREIGN KEY (`idPessoa`) REFERENCES pessoa(`idPessoa`));